import React, { Component } from 'react';
import { render } from 'react-dom';
import { Map, TileLayer } from 'react-leaflet';
import Control from 'react-leaflet-control';

const stamenTonerTiles = 'https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png';
// Ejemplos de mapas https://leaflet-extras.github.io/leaflet-providers/preview/
const stamenTonerAttr = 'Map tiles by <a href="http://stamen.com">Stamen Design</a>, <a href="http://creativecommons.org/licenses/by/3.0">CC BY 3.0</a> &mdash; Map data &copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>';
const mapCenter = [40.1301919, -8.2008354];
const zoomLevel = 3;


/* Circuit Array */
var listCircuit = [];

/* LINK api Circuits F1*/
var url = 'https://ergast.com/api/f1/circuits.json?limit=100';


/* AJAX Request */
var resultados = [];

function peticionAjax(url, callback) { 
    var peticion = new XMLHttpRequest();

    peticion.open('GET', url);
    peticion.onreadystatechange = function () {
        if (this.readyState === 4) {
          callback(this.responseText); 
        }
      };
    peticion.send();
}

