import React from 'react';
import './App.scss';

import Map from './components/map/map';
import Content from './components/content/content';

class App extends React.Component {

  constructor(){
    super()
    this.state = {
      year: '2019',
      circuits: [],
      circuitsYears: [],
      results: []
    }
  }


  componentDidMount(){
    this.getListCircuits();
    this.getYearCircuits();
  }

  getListCircuits() {
    fetch('http://ergast.com/api/f1/' + this.state.year + '.json?limit=100')
      .then((response) => {
        return response.json()
      })
      .then((yearsCircuits) => {
        this.setState({ circuitsYears: yearsCircuits.MRData.RaceTable.Races})
      })
  }

  getYearCircuits () {
    fetch('http://ergast.com/api/f1/' + this.state.year + '/circuits.json?limit=100')
        .then((response) => {
        return response.json()
        })
        .then((resultados) => {
        this.setState({ circuits: resultados.MRData.CircuitTable.Circuits})
    })
}




  handleYearChange = event => {
    const year = event.target.value;
    this.setState({ year });
  }


  render (){

    const { year } = this.state;

    return (
      <div className="App">
        <header className="App-header">
          <Map year={year}/> 
          <Content year={year} handleYearChange={this.handleYearChange} />
        </header>      
      </div>
    );
  }
}

export default App;
