import React, { Component } from 'react';



export default class App extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            seasonsCircuits: []
        };
    }


    componentWillMount() {
        fetch('https://ergast.com/api/f1/seasons.json?limit=100')
          .then((response) => {
            return response.json()
          })
          .then((seasonsCircuits) => {
            this.setState({ seasonsCircuits: seasonsCircuits.MRData.SeasonTable.season})
          })
      }   



    render() {

        const { seasonsCircuits } = this.state;

        return (
            <div>
                <div >
                    <select>
                    {seasonsCircuits.map((seasonsCircuits) => {
                        return (
                            <option>{ seasonsCircuits.season }</option>                        
                        )
                        })
                    }

                    </select>
                </div>
            </div>
        );
    }
}
